INSERT INTO `movie_locations` VALUES (1061,257,'California Palace of the Legion of Honor (34th Avenue & Clement, Lincoln Park)',37.7749295,-122.4194155,'Built in 1924, the Legion of Honor is a 3/4 replica of the Parisian Palais de la Legion d''Honneur.');
INSERT INTO `movie_locations` VALUES (1062,257,'Claude Lane at Bush Street',37.7907118,-122.404452,NULL);
INSERT INTO `movie_locations` VALUES (1063,257,'Conservatory of Flowers (Golden Gate Park)',37.772614,-122.460253,'The Conservatory, unveiled in 1879, is the oldest public conservatory in the Western Hemisphere.');
INSERT INTO `movie_locations` VALUES (1064,257,'Ernie''s Restaurant (847 Montgomery Street)',37.7969655,-122.4036573,'Called the "Essex Club" in the film.');
INSERT INTO `movie_locations` VALUES (1065,257,'Fairmont Hotel (950 Mason Street, Nob Hill)',37.7923317,-122.4104804,'In 1945 the Fairmont hosted the United Nations Conference on International Organization as delegates arrived to draft a charter for the organization. The U.S. Secretary of State, Edward Stettinus drafted the charter in the hotel''s Garden Room.');
INSERT INTO `movie_locations` VALUES (1066,257,'Fort Point (Presidio, Golden Gate National Recreation Area)',37.7749295,-122.4194155,'Built in 1853, Fort Point is the only West Coast fort built by the Union Army. Though the Confederate army planned to attack San Francisco, the commanding Confererate general learned that the war had ended while en route to SF.');
INSERT INTO `movie_locations` VALUES (1067,257,'Gough & Eddy Streets (Western Addition)',37.7824876,-122.4241111,NULL);
INSERT INTO `movie_locations` VALUES (1068,257,'Mission Delores (3321 16th Street, Mision District)',37.7643856,-122.4275673,'Mission Delores'' official name is Mission San Francisco de Asis. It is the oldest building in San Francisco, built in 1791, and has survived two major earthquakes.');
INSERT INTO `movie_locations` VALUES (1069,257,'Mission San Juan Bautista (2nd & Mariposa Streets)',37.763643,-122.4005248,NULL);
