INSERT INTO `movie_locations` VALUES (1072,257,'Palace of Fine Arts (3301 Lyon Street)',37.8018183,-122.4482175,'The original Palace was built for the 1915 Panama-Pacific Exposition, and completely destroyed in 1964. It was rebuilt in 1965.');
INSERT INTO `movie_locations` VALUES (1073,258,'Treasure Island',37.8235515,-122.370648,'An artificial island, Treasure Island was created for the 1939 Golden Gate International Exposition, and is named after the novel by Robert Louis Stevenson, a one-time San Francisco resident.');
INSERT INTO `movie_locations` VALUES (1074,259,'Alta Plaza Park (Steiner Street)',37.7849566,-122.4347061,'The park was originally a rock quarry and served as a campground for many survivors of the 1906 earthquake. The site was converted to a park in 1910.');
